stochastic_function:
lambda age, maximum_coverage_per_day, insurance_premium_per_year, long_term_care_expense_per_day, life_expectancy: { 
    -((life_expectancy - age) * (365 * long_term_care_expense_per_day + insurance_premium_per_year - 365 * maximum_coverage_per_day)): 1 
} if long_term_care_expense_per_day > maximum_coverage_per_day else { 
    -((life_expectancy - age) * insurance_premium_per_year): 1 
}
