stochastic_function:
lambda age, maximum_coverage_per_day, insurance_premium_per_year, long_term_care_expense_per_day, life_expectancy: { 
    -1 * ((life_expectancy - age) * (365 * long_term_care_expense_per_day - 365 * maximum_coverage_per_day + insurance_premium_per_year)): 1 
    } if long_term_care_expense_per_day > maximum_coverage_per_day else { 
    -1 * ((life_expectancy - age) * (insurance_premium_per_year)): 1 
}
